import React from "react";
import { Container, Section, Button } from "../index";
import "./style.scss";

function Hero({title, desc}) {
	return (
		<div className="hero">
			<Container>
				<Section>
					<h1>{title}</h1>
					<p>{desc}</p>
					{buttonLabel && (
						<Button label="Click here" icon="check" />
					)}
				</Section>
			</Container>
		</div>
	);
}

export default Hero;
