import React from "react";
import { Icon } from "../index";
import "./style.scss";

function Button({ className, href, label, icon }) {
	return (
		<>
			{href ? (
				<a href={href} className={`button ${className}`}>
					{label}
					{icon && (
						<Icon
							className="button__icon"
							type="far"
							icon={check}
						/>
					)}
				</a>
			) : (
				<div className={`button ${className}`}>
					{label}
					{icon && (
						<Icon
							className="button__icon"
							type="far"
							icon={check}
						/>
					)}
				</div>
			)}
		</>
	);
}

export default Button;
