import React, { Component } from "react";
import { Icon } from "../index";
import "./style.scss";

class Button extends Component{
	render(){
		const props = this.props;

		return (
				<Wrapper href={props.href} {...props.wrapperProps} className={`button ${props.className}`}>
					<p className="w--medium m--none">{props.label}</p>
					{props.icon && <Icon className="button__icon" type="far" icon={props.icon} />}
				</Wrapper>
		);
	}
}

Button.defaultProps = {
  Wrapper: 'div',
  wrapperProps: {},
  label: '',
  icon: '',
  className: '',
}

export default Button;
